import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [isVisible, setIsVisible] = useState(false);
  const [flickerIntensity, setFlickerIntensity] = useState(0);
  const [opacity, setOpacity] = useState(0);

  useEffect(() => {
    // Start the effect after a short delay
    const startTimer = setTimeout(() => {
      setIsVisible(true);
    }, 1000);

    return () => clearTimeout(startTimer);
  }, []);

  useEffect(() => {
    if (!isVisible) return;

    // Gradually increase opacity and flicker intensity
    const opacityInterval = setInterval(() => {
      setOpacity(prev => {
        if (prev >= 1) {
          clearInterval(opacityInterval);
          return 1;
        }
        return prev + 0.02;
      });
    }, 100);

    // Flicker effect
    const flickerInterval = setInterval(() => {
      setFlickerIntensity(prev => {
        if (opacity >= 1) {
          // Continue flickering even after fully visible
          return Math.random() * 0.3 + 0.7;
        }
        return Math.random() * 0.5 + 0.3;
      });
    }, 50);

    return () => {
      clearInterval(opacityInterval);
      clearInterval(flickerInterval);
    };
  }, [isVisible, opacity]);

  return (
    <div className="App">
      <div 
        className="misfits-text"
        style={{
          opacity: opacity * flickerIntensity,
          transform: `scale(${1 + (flickerIntensity - 0.8) * 0.1})`
        }}
      >
        MISFITS
      </div>
    </div>
  );
}

export default App;
