import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [visibleLetters, setVisibleLetters] = useState([]);
  const [flickerIntensity, setFlickerIntensity] = useState(0);
  const [currentLetterIndex, setCurrentLetterIndex] = useState(0);

  const word = "MISFITS";
  const neonColors = ['#ff0080', '#00ff80', '#0080ff', '#ff8000', '#8000ff', '#ff0080', '#00ff80'];

  useEffect(() => {
    // Start the effect after a short delay
    const startTimer = setTimeout(() => {
      setVisibleLetters([]);
      setCurrentLetterIndex(0);
    }, 1000);

    return () => clearTimeout(startTimer);
  }, []);

  useEffect(() => {
    if (currentLetterIndex >= word.length) return;

    // Show each letter one by one
    const letterTimer = setTimeout(() => {
      setVisibleLetters(prev => [...prev, currentLetterIndex]);
      setCurrentLetterIndex(prev => prev + 1);
    }, 800); // 800ms delay between each letter

    return () => clearTimeout(letterTimer);
  }, [currentLetterIndex, word.length]);

  useEffect(() => {
    if (visibleLetters.length === 0) return;

    // Flicker effect for visible letters
    const flickerInterval = setInterval(() => {
      setFlickerIntensity(Math.random() * 0.4 + 0.6);
    }, 100);

    return () => clearInterval(flickerInterval);
  }, [visibleLetters.length]);

  return (
    <div className="App">
      <div className="misfits-text">
        {word.split('').map((letter, index) => (
          <span
            key={index}
            className="letter"
            style={{
              opacity: visibleLetters.includes(index) ? flickerIntensity : 0,
              color: neonColors[index % neonColors.length],
              textShadow: visibleLetters.includes(index) 
                ? `0 0 10px ${neonColors[index % neonColors.length]}, 0 0 20px ${neonColors[index % neonColors.length]}, 0 0 30px ${neonColors[index % neonColors.length]}`
                : 'none',
              transform: visibleLetters.includes(index) 
                ? `scale(${1 + (flickerIntensity - 0.8) * 0.1})` 
                : 'scale(0.8)',
              transition: 'all 0.1s ease-in-out'
            }}
          >
            {letter}
          </span>
        ))}
      </div>
    </div>
  );
}

export default App;
