import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [visibleLetters, setVisibleLetters] = useState([]);
  const [flickerIntensity, setFlickerIntensity] = useState(0);
  const [currentLetterIndex, setCurrentLetterIndex] = useState(0);
  const [isStarted, setIsStarted] = useState(false);
  const [letterSharpness, setLetterSharpness] = useState({});
  const [startTime, setStartTime] = useState(null);
  const [flickerDecay, setFlickerDecay] = useState(1); // 1 = full flicker, 0 = no flicker

  const word = "MISFITS";
  const neonColors = ['#ff0080', '#00ff80', '#0080ff', '#ff8000', '#8000ff', '#ff0080', '#00ff80'];

  useEffect(() => {
    // Start the effect after a short delay
    const startTimer = setTimeout(() => {
      setIsStarted(true);
      setVisibleLetters([]);
      setCurrentLetterIndex(0);
      setLetterSharpness({});
      setStartTime(Date.now());
      setFlickerDecay(1); // Start with full flicker
    }, 1500);

    return () => clearTimeout(startTimer);
  }, []);

  useEffect(() => {
    if (!isStarted || currentLetterIndex >= word.length) return;

    // Show each letter one by one with faster timing
    const letterTimer = setTimeout(() => {
      setVisibleLetters(prev => [...prev, currentLetterIndex]);
      setCurrentLetterIndex(prev => prev + 1);
    }, 300); // Faster timing for better flow

    return () => clearTimeout(letterTimer);
  }, [currentLetterIndex, word.length, isStarted]);

  useEffect(() => {
    if (visibleLetters.length === 0 || !startTime) return;

    // Gradually reduce flicker and sharpen letters over time
    const transitionInterval = setInterval(() => {
      const elapsed = (Date.now() - startTime) / 1000; // Time in seconds
      const totalDuration = 15; // 15 seconds total for the entire effect

      if (elapsed >= totalDuration) {
        // After 15 seconds, everything is clear and stable
        setFlickerDecay(0);
        setLetterSharpness(prev => {
          const newSharpness = { ...prev };
          visibleLetters.forEach(letterIndex => {
            newSharpness[letterIndex] = 1; // Fully sharp
          });
          return newSharpness;
        });
        clearInterval(transitionInterval);
      } else {
        // Gradually reduce flicker and increase sharpness
        const progress = elapsed / totalDuration;

        // Flicker decay: starts at 1, goes to 0 over time
        setFlickerDecay(Math.max(0, 1 - progress));

        // Letter sharpness: gradually increase
        setLetterSharpness(prev => {
          const newSharpness = { ...prev };
          visibleLetters.forEach(letterIndex => {
            if (!newSharpness[letterIndex]) {
              newSharpness[letterIndex] = 0;
            }
            newSharpness[letterIndex] = Math.min(1, newSharpness[letterIndex] + 0.01);
          });
          return newSharpness;
        });
      }
    }, 50);

    return () => clearInterval(transitionInterval);
  }, [visibleLetters, startTime]);

  useEffect(() => {
    if (visibleLetters.length === 0) return;

    // Flicker effect that gradually slows down
    const flickerInterval = setInterval(() => {
      const baseFlicker = Math.random() * 0.4 + 0.6;
      const adjustedFlicker = baseFlicker * flickerDecay; // Scale by decay
      setFlickerIntensity(adjustedFlicker);
    }, 100 + (1 - flickerDecay) * 200); // Slower updates as flicker decays

    return () => clearInterval(flickerInterval);
  }, [visibleLetters.length, flickerDecay]);

  return (
    <div className="App">
      <div className="misfits-text">
        {word.split('').map((letter, index) => {
          const isVisible = visibleLetters.includes(index);
          const sharpness = letterSharpness[index] || 0;
          const blurAmount = isVisible ? (1 - sharpness) * 30 : 30; // Start blurry
          const opacity = isVisible ? (0.3 + sharpness * 0.7) : 0; // Gradually become visible
          const brightness = isVisible ? (0.3 + sharpness * 0.7) : 0.3; // Gradually brighten

          return (
            <span
              key={index}
              className={`letter ${isVisible ? 'visible' : ''}`}
              style={{
                opacity: opacity,
                color: neonColors[index % neonColors.length],
                textShadow: isVisible 
                  ? `0 0 10px ${neonColors[index % neonColors.length]}, 0 0 20px ${neonColors[index % neonColors.length]}, 0 0 30px ${neonColors[index % neonColors.length]}, 0 0 40px ${neonColors[index % neonColors.length]}`
                  : 'none',
                transform: isVisible 
                  ? `scale(${1 + (flickerIntensity - 0.7) * 0.05}) translateY(${(flickerIntensity - 0.7) * 2}px)` 
                  : 'scale(0.9) translateY(10px)',
                filter: `blur(${blurAmount}px) brightness(${brightness})`,
                transition: 'all 0.2s ease-out'
              }}
            >
              {letter}
            </span>
          );
        })}
      </div>
    </div>
  );
}

export default App;
